-        if (ctxt->input->cur[tlen] == '>') {
-	    ctxt->input->cur += tlen + 1;
-	    ctxt->input->col += tlen + 1;
-	    goto done;
-	}
-	ctxt->input->cur += tlen;
-	ctxt->input->col += tlen;
-	name = (xmlChar*)1;
+        /* Ensure we don't read past the end of the buffer */
+        if (ctxt->input->cur + tlen < ctxt->input->end) {
+            if (ctxt->input->cur[tlen] == '>') {
+                ctxt->input->cur += tlen + 1;
+                ctxt->input->col += tlen + 1;
+                goto done;
+            }
+        } else {
+            GROW;
+            if (ctxt->input->cur + tlen < ctxt->input->end) {
+                if (ctxt->input->cur[tlen] == '>') {
+                    ctxt->input->cur += tlen + 1;
+                    ctxt->input->col += tlen + 1;
