index 82b0eeaf..0e38845c 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10056,6 +10056,7 @@ xmlXPathParseNameComplex(xmlXPathParserContextPtr ctxt, int qualified) {
 }

 #define MAX_FRAC 20
+#define MAX_EXPONENT 1000

