  * xmlXPathStringEvalNumber:
@@ -10151,6 +10152,10 @@ xmlXPathStringEvalNumber(const xmlChar *str) {
         cur++;
       }
       while ((*cur >= '0') && (*cur <= '9')) {
+	if (exponent > MAX_EXPONENT) {
+	    /* Prevent integer overflow */
+	    break;
+	}
 	exponent = exponent * 10 + (*cur - '0');
 	cur++;
       }
@@ -10245,6 +10250,10 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
 	    NEXT;
 	}
         while ((CUR >= '0') && (CUR <= '9')) {
+            if (exponent > MAX_EXPONENT) {
+                /* Prevent integer overflow */
+                break;
+            }
             exponent = exponent * 10 + (CUR - '0');
             NEXT;
         }

