patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..9d7183c3 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10245,8 +10245,19 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
 	    NEXT;
 	}
         while ((CUR >= '0') && (CUR <= '9')) {
-            exponent = exponent * 10 + (CUR - '0');
-            NEXT;
+            /* Check for overflow before multiplying and adding */
+            if (exponent > (INT_MAX / 10) ||
+                (exponent == (INT_MAX / 10) && (CUR - '0') > (INT_MAX % 10))) {
+                exponent = INT_MAX;
+                /* Consume remaining digits to keep parsing consistent */
+                while ((CUR >= '0') && (CUR <= '9')) {
+                    NEXT;
+                }
+                break;
+            } else {
+                exponent = exponent * 10 + (CUR - '0');
+                NEXT;
+            }
         }
         if (is_exponent_negative)
             exponent = -exponent;
