patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..c426c886 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10245,7 +10245,17 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
 	    NEXT;
 	}
         while ((CUR >= '0') && (CUR <= '9')) {
-            exponent = exponent * 10 + (CUR - '0');
+            int digit = CUR - '0';
+            if (exponent > (INT_MAX - digit) / 10) {
+                /* Clamp to INT_MAX to avoid overflow */
+                exponent = INT_MAX;
+                /* Consume remaining digits to keep parsing position correct */
+                while ((CUR >= '0') && (CUR <= '9')) {
+                    NEXT;
+                }
+                break;
+            }
+            exponent = exponent * 10 + digit;
             NEXT;
         }
         if (is_exponent_negative)
