patch:
diff --git a/xpath.c b/xpath.c
index 82b0eeaf..79305c7b 100644
--- a/xpath.c
+++ b/xpath.c
@@ -10245,8 +10245,18 @@ xmlXPathCompNumber(xmlXPathParserContextPtr ctxt)
 	    NEXT;
 	}
         while ((CUR >= '0') && (CUR <= '9')) {
-            exponent = exponent * 10 + (CUR - '0');
-            NEXT;
+            /* Check for potential overflow before multiplying */
+            if (exponent > (INT_MAX - 9) / 10) {
+                /* Clamp to INT_MAX and consume remaining digits */
+                exponent = INT_MAX;
+                while ((CUR >= '0') && (CUR <= '9')) {
+                    NEXT;
+                }
+                break;
+            } else {
+                exponent = exponent * 10 + (CUR - '0');
+                NEXT;
+            }
         }
         if (is_exponent_negative)
             exponent = -exponent;

