patch:
diff --git a/shell.c b/shell.c
index 8ecc05b9..72ea1512 100644
--- a/shell.c
+++ b/shell.c
@@ -1128,6 +1128,8 @@ xmllintShell(xmlDoc *doc, const char *filename, FILE * output)
         i = 0;
         while ((*cur != ' ') && (*cur != '\t') &&
                (*cur != '\n') && (*cur != '\r')) {
+            if (i >= sizeof(command) - 1)
+                break;
             if (*cur == 0)
                 break;
             command[i++] = *cur++;
@@ -1143,6 +1145,8 @@ xmllintShell(xmlDoc *doc, const char *filename, FILE * output)
             cur++;
         i = 0;
         while ((*cur != '\n') && (*cur != '\r') && (*cur != 0)) {
+            if (i >= sizeof(arg) - 1)
+                break;
             if (*cur == 0)
                 break;
             arg[i++] = *cur++;
