-        while ((*cur != '\n') && (*cur != '\r') && (*cur != 0)) {
+        while ((*cur != '\n') && (*cur != '\r') && (*cur != 0) && (i < (int)sizeof(arg) - 1)) {
             if (*cur == 0)
                 break;
             arg[i++] = *cur++;

