patch:
diff --git a/shell.c b/shell.c
index 8ecc05b9..0685ed3f 100644
--- a/shell.c
+++ b/shell.c
@@ -1127,7 +1127,7 @@ xmllintShell(xmlDoc *doc, const char *filename, FILE * output)
             cur++;
         i = 0;
         while ((*cur != ' ') && (*cur != '\t') &&
-               (*cur != '\n') && (*cur != '\r')) {
+               (*cur != '\n') && (*cur != '\r') && (i < (int)sizeof(command) - 1)) {
             if (*cur == 0)
                 break;
             command[i++] = *cur++;
@@ -1142,7 +1142,7 @@ xmllintShell(xmlDoc *doc, const char *filename, FILE * output)
         while ((*cur == ' ') || (*cur == '\t'))
             cur++;
         i = 0;
-        while ((*cur != '\n') && (*cur != '\r') && (*cur != 0)) {
+        while ((*cur != '\n') && (*cur != '\r') && (*cur != 0) && (i < (int)sizeof(arg) - 1)) {
             if (*cur == 0)
                 break;
             arg[i++] = *cur++;
