patch:
diff --git a/shell.c b/shell.c
index 8ecc05b9..ad1445e8 100644
--- a/shell.c
+++ b/shell.c
@@ -1130,6 +1130,8 @@ xmllintShell(xmlDoc *doc, const char *filename, FILE * output)
                (*cur != '\n') && (*cur != '\r')) {
             if (*cur == 0)
                 break;
+            if (i >= (int)sizeof(command) - 1)
+                break;
             command[i++] = *cur++;
         }
         command[i] = 0;
@@ -1145,6 +1147,8 @@ xmllintShell(xmlDoc *doc, const char *filename, FILE * output)
         while ((*cur != '\n') && (*cur != '\r') && (*cur != 0)) {
             if (*cur == 0)
                 break;
+            if (i >= (int)sizeof(arg) - 1)
+                break;
             arg[i++] = *cur++;
         }
         arg[i] = 0;
