diff --git a/src/crwimage_int.cpp b/src/crwimage_int.cpp
index c2fd5f3a5..e0d078efd 100644
--- a/src/crwimage_int.cpp
+++ b/src/crwimage_int.cpp
@@ -281,7 +281,11 @@ namespace Exiv2 {
         if (size < 4)
             throw Error(kerCorruptedMetadata);
         uint32_t o = getULong(pData + size - 4, byteOrder);
-        if ( o+2 > size )
+// FIXME: Crash CWE-125: Out-of-bounds Read
+         if ( o+2 > size )
+            throw Error(kerCorruptedMetadata);
+        /* Check: ensure o+2 does not overflow 32-bit, and bounds for getUShort */
+        if (o > size - 2)
             throw Error(kerCorruptedMetadata);
         uint16_t count = getUShort(pData + o, byteOrder);
 #ifdef DEBUG
@@ -293,6 +297,9 @@ namespace Exiv2 {
             throw Error(kerCorruptedMetadata);

         for (uint16_t i = 0; i < count; ++i) {
+            /* Ensure we can read tag: prevent OOB on each iteration */
+            if (o > size - 2)
+                throw Error(kerCorruptedMetadata);
             uint16_t tag = getUShort(pData + o, byteOrder);
             CiffComponent::AutoPtr m;
             switch (CiffComponent::typeId(tag)) {
