patch:
diff --git a/xmpsdk/src/XMPUtils.cpp b/xmpsdk/src/XMPUtils.cpp
index 8474b8dd3..de7ad73ab 100644
--- a/xmpsdk/src/XMPUtils.cpp
+++ b/xmpsdk/src/XMPUtils.cpp
@@ -2077,8 +2077,14 @@ XMPUtils::ConvertToLocalTime ( XMP_DateTime * time )
 	XMP_Assert ( (0 <= time->tzHour) && (time->tzHour <= 23) );
 	XMP_Assert ( (0 <= time->tzMinute) && (time->tzMinute <= 59) );
 	XMP_Assert ( (-1 <= time->tzSign) && (time->tzSign <= +1) );
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
 	XMP_Assert ( (time->tzSign == 0) ? ((time->tzHour == 0) && (time->tzMinute == 0)) :
-									   ((time->tzHour != 0) || (time->tzMinute != 0)) );
+								   ((time->tzHour != 0) || (time->tzMinute != 0)) );
+	/* Integer overflow fix: Validate 'year' before possible processing in called functions */
+	if (time->year < 1900) {
+		/* Handle invalid value: set to 1900 as default or raise debug assertion */
+		time->year = 1900;
+	}

 	ConvertToUTCTime ( time );	// The existing time zone might not be the local one.
 	SetTimeZone ( time );		// Fill in the local timezone offset, then adjust the time.
