patch:
diff --git a/xmpsdk/include/client-glue/TXMPUtils.incl_cpp b/xmpsdk/include/client-glue/TXMPUtils.incl_cpp
index 6898be73b..18e12b580 100644
--- a/xmpsdk/include/client-glue/TXMPUtils.incl_cpp
+++ b/xmpsdk/include/client-glue/TXMPUtils.incl_cpp
@@ -302,25 +302,42 @@ SetTimeZone ( XMP_DateTime * time )
 XMP_MethodIntro(TXMPUtils,void)::
 ConvertToUTCTime ( XMP_DateTime * time )
 {
-	WrapCheckVoid ( zXMPUtils_ConvertToUTCTime_1 ( time ) );
+	if (time != NULL && time->year >= 1900 && time->year <= 9999) {
+		WrapCheckVoid ( zXMPUtils_ConvertToUTCTime_1 ( time ) );
+	} else {
+		/* Prevent integer overflow in year arithmetic */
+		return;
+	}
 }

 // -------------------------------------------------------------------------------------------------

 XMP_MethodIntro(TXMPUtils,void)::
-ConvertToLocalTime ( XMP_DateTime * time )
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+ ConvertToLocalTime ( XMP_DateTime * time )
 {
-	WrapCheckVoid ( zXMPUtils_ConvertToLocalTime_1 ( time ) );
+	if (time != NULL && time->year >= 1900 && time->year <= 9999) {
+		WrapCheckVoid ( zXMPUtils_ConvertToLocalTime_1 ( time ) );
+	} else {
+		/* Prevent integer overflow in year arithmetic */
+		return;
+	}
 }

 // -------------------------------------------------------------------------------------------------

 XMP_MethodIntro(TXMPUtils,int)::
 CompareDateTime ( const XMP_DateTime & left,
-				  const XMP_DateTime & right )
-{
-	WrapCheckInt32 ( result, zXMPUtils_CompareDateTime_1 ( left, right ) );
-	return result;
+			  const XMP_DateTime & right )
+{
+	int result = 0;
+	if (left.year >= 1900 && left.year <= 9999 && right.year >= 1900 && right.year <= 9999) {
+		WrapCheckInt32 ( result, zXMPUtils_CompareDateTime_1 ( left, right ) );
+		return result;
+	} else {
+		/* Prevent integer overflow in year arithmetic */
+		return 0;
+	}
 }

 // -------------------------------------------------------------------------------------------------
