patch:
diff --git a/src/tags_int.cpp b/src/tags_int.cpp
index f9a3ea287..b8196f9a1 100644
--- a/src/tags_int.cpp
+++ b/src/tags_int.cpp
@@ -2774,8 +2774,9 @@ namespace Exiv2 {
             int p = 0;
             if (sec != static_cast<int>(sec)) p = 1;

-            const int hh = static_cast<int>(sec / 3600);
-            const int mm = static_cast<int>((sec - 3600 * hh) / 60);
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+             const long long hh = static_cast<long long>(sec / 3600);
+            const long long mm = static_cast<long long>((sec - 3600 * hh) / 60);
             const float ss = sec - 3600 * hh - 60 * mm;

             os << std::setw(2) << std::setfill('0') << std::right << hh << ":"
