patch:
diff --git a/src/tags_int.cpp b/src/tags_int.cpp
index f9a3ea287..f9e129ca5 100644
--- a/src/tags_int.cpp
+++ b/src/tags_int.cpp
@@ -2768,11 +2768,24 @@ namespace Exiv2 {
             }
             std::ostringstream oss;
             oss.copyfmt(os);
-            const float sec = 3600 * value.toFloat(0)
-                              + 60 * value.toFloat(1)
-                              + value.toFloat(2);
+            // Check for potential overflow before doing the arithmetic
+            const float v0 = value.toFloat(0);
+            const float v1 = value.toFloat(1);
+            const float v2 = value.toFloat(2);
+            if (v0 > (std::numeric_limits<int>::max() / 3600.0f)
+                || v1 > (std::numeric_limits<int>::max() / 60.0f)
+                || v2 > std::numeric_limits<int>::max()
+                || v0 < (std::numeric_limits<int>::min() / 3600.0f)
+                || v1 < (std::numeric_limits<int>::min() / 60.0f)
+                || v2 < std::numeric_limits<int>::min()) {
+                return os << "(" << value << ")";
+            }
+            const float sec = 3600 * v0
+                              + 60 * v1
+                              + v2;
             int p = 0;
             if (sec != static_cast<int>(sec)) p = 1;
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound

             const int hh = static_cast<int>(sec / 3600);
             const int mm = static_cast<int>((sec - 3600 * hh) / 60);
