patch:
diff --git a/src/tags_int.cpp b/src/tags_int.cpp
index f9a3ea287..f96dee023 100644
--- a/src/tags_int.cpp
+++ b/src/tags_int.cpp
@@ -2768,15 +2768,17 @@ namespace Exiv2 {
             }
             std::ostringstream oss;
             oss.copyfmt(os);
-            const float sec = 3600 * value.toFloat(0)
-                              + 60 * value.toFloat(1)
-                              + value.toFloat(2);
+            // Use double for sec to prevent integer overflow
+            const double sec = 3600.0 * static_cast<double>(value.toFloat(0))
+                              + 60.0 * static_cast<double>(value.toFloat(1))
+                              + static_cast<double>(value.toFloat(2));
             int p = 0;
             if (sec != static_cast<int>(sec)) p = 1;
-
-            const int hh = static_cast<int>(sec / 3600);
-            const int mm = static_cast<int>((sec - 3600 * hh) / 60);
-            const float ss = sec - 3600 * hh - 60 * mm;
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+ 
+            const int hh = static_cast<int>(sec / 3600.0);
+            const int mm = static_cast<int>((sec - 3600.0 * hh) / 60.0);
+            const double ss = sec - 3600.0 * hh - 60.0 * mm;

             os << std::setw(2) << std::setfill('0') << std::right << hh << ":"
                << std::setw(2) << std::setfill('0') << std::right << mm << ":"
