patch:
diff --git a/src/tags_int.cpp b/src/tags_int.cpp
index f9a3ea287..d87249625 100644
--- a/src/tags_int.cpp
+++ b/src/tags_int.cpp
@@ -2768,12 +2768,18 @@ namespace Exiv2 {
             }
             std::ostringstream oss;
             oss.copyfmt(os);
-            const float sec = 3600 * value.toFloat(0)
-                              + 60 * value.toFloat(1)
+            // Use double to hold potentially larger sec value and add bounds check
+            const double sec = 3600.0 * value.toFloat(0)
+                              + 60.0 * value.toFloat(1)
                               + value.toFloat(2);
+            if (sec > static_cast<double>(std::numeric_limits<int>::max()) ||
+                sec < static_cast<double>(std::numeric_limits<int>::min())) {
+                return os << "(overflow)";
+            }
             int p = 0;
             if (sec != static_cast<int>(sec)) p = 1;
-
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+ 
             const int hh = static_cast<int>(sec / 3600);
             const int mm = static_cast<int>((sec - 3600 * hh) / 60);
             const float ss = sec - 3600 * hh - 60 * mm;
