@@ -686,13 +686,19 @@ namespace Exiv2 {
                 os << value << N_(" hPa");
                 break;
           };
-          return os;
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+           return os;
     } // PanasonicMakerNote::printPressure

     std::ostream& PanasonicMakerNote::printAccelerometer(std::ostream& os, const Value& value, const ExifData*)
     {
         // value is stored as unsigned int, but should be readed as signed int, so manually convert it
         int i = value.toLong();
+        // Check for potential overflow before subtraction
+        if (((i & 0x8000) >> 15) && (i < 0x8000 || i - 0xffff < INT_MIN)) {
+            // Handle overflow, print error or clamp
+            return os << "Overflow";
+        }
         i = i - ((i & 0x8000) >> 15) * 0xffff;
         return os << i;
     }  // PanasonicMakerNote::printAccelerometer
