patch:
diff --git a/src/H5VLcallback.c b/src/H5VLcallback.c
index 58e839c998..cb96a44a6c 100644
--- a/src/H5VLcallback.c
+++ b/src/H5VLcallback.c
@@ -5101,15 +5101,19 @@ H5VL_link_move(const H5VL_object_t *src_vol_obj, const H5VL_loc_params_t *loc_pa
     FUNC_ENTER_NOAPI(FAIL)

     /* Set wrapper info in API context */
-    vol_obj = (src_vol_obj->data ? src_vol_obj : dst_vol_obj);
+    if(src_vol_obj == NULL && dst_vol_obj == NULL) {
+        HGOTO_ERROR(H5E_VOL, H5E_BADVALUE, FAIL, "both src_vol_obj and dst_vol_obj are NULL");
+    }
+    vol_obj = (src_vol_obj && src_vol_obj->data ? src_vol_obj : dst_vol_obj);
     if (H5VL_set_vol_wrapper(vol_obj) < 0)
         HGOTO_ERROR(H5E_VOL, H5E_CANTSET, FAIL, "can't set VOL wrapper info");
     vol_wrapper_set = true;

     /* Call the corresponding internal VOL routine */
-    if (H5VL__link_move(src_vol_obj->data, loc_params1, (dst_vol_obj ? dst_vol_obj->data : NULL), loc_params2,
+    if (H5VL__link_move(src_vol_obj && src_vol_obj->data ? src_vol_obj->data : NULL, loc_params1, (dst_vol_obj ? dst_vol_obj->data : NULL), loc_params2,
                         vol_obj->connector->cls, lcpl_id, lapl_id, dxpl_id, req) < 0)
-        HGOTO_ERROR(H5E_VOL, H5E_CANTMOVE, FAIL, "link move failed");
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+         HGOTO_ERROR(H5E_VOL, H5E_CANTMOVE, FAIL, "link move failed");

 done:
     /* Reset object wrapping info in API context */
