patch:
diff --git a/src/H5Shyper.c b/src/H5Shyper.c
index 11202da8a6..2c58b9e308 100644
--- a/src/H5Shyper.c
+++ b/src/H5Shyper.c
@@ -10697,9 +10697,11 @@ H5S__combine_select(H5S_t *space1, H5S_seloper_t op, H5S_t *space2)
         if (H5S__fill_in_select(space1, op, space2, &new_space) < 0)
             HGOTO_ERROR(H5E_DATASPACE, H5E_CANTCLIP, NULL, "can't clip hyperslab information");
     } /* end else */
+// FIXME: Crash CWE-824: Access of Uninitialized Pointer

-    /* Set unlim_dim */
-    new_space->select.sel_info.hslab->unlim_dim = -1;
+    /* Defensive: check new_space is non-NULL before use */
+    if(new_space && new_space->select.sel_info.hslab)
+        new_space->select.sel_info.hslab->unlim_dim = -1;

     /* Set return value */
     ret_value = new_space;
