patch:
diff --git a/src/H5Shyper.c b/src/H5Shyper.c
index 11202da8a6..b7b2f74442 100644
--- a/src/H5Shyper.c
+++ b/src/H5Shyper.c
@@ -10689,15 +10689,19 @@ H5S__combine_select(H5S_t *space1, H5S_seloper_t op, H5S_t *space2)
         } /* end for */

         /* Combine hyperslab selection with regular selection directly */
-        if (H5S_combine_hyperslab(space1, op, tmp_start, tmp_stride, tmp_count, tmp_block, &new_space) < 0)
-            HGOTO_ERROR(H5E_DATASPACE, H5E_CANTINIT, NULL, "unable to set hyperslab selection");
-    } /* end if */
-    else {
-        /* Combine new_space (a copy of space 1) & space2, with the result in new_space */
-        if (H5S__fill_in_select(space1, op, space2, &new_space) < 0)
-            HGOTO_ERROR(H5E_DATASPACE, H5E_CANTCLIP, NULL, "can't clip hyperslab information");
-    } /* end else */
-
+    if (H5S_combine_hyperslab(space1, op, tmp_start, tmp_stride, tmp_count, tmp_block, &new_space) < 0)
+        HGOTO_ERROR(H5E_DATASPACE, H5E_CANTINIT, NULL, "unable to set hyperslab selection");
+} /* end if */
+else {
+    /* Combine new_space (a copy of space 1) & space2, with the result in new_space */
+    if (H5S__fill_in_select(space1, op, space2, &new_space) < 0)
+        HGOTO_ERROR(H5E_DATASPACE, H5E_CANTCLIP, NULL, "can't clip hyperslab information");
+} /* end else */
+// FIXME: Crash CWE-824: Access of Uninitialized Pointer
+
+    /* Protect against uninitialized new_space */
+    if (!new_space || !(new_space->select.sel_info.hslab))
+        HGOTO_ERROR(H5E_DATASPACE, H5E_CANTINIT, NULL, "new_space not properly initialized");
     /* Set unlim_dim */
     new_space->select.sel_info.hslab->unlim_dim = -1;
