patch:
diff --git a/src/H5Shyper.c b/src/H5Shyper.c
index 7d6739a7fb..1a4f94712e 100644
--- a/src/H5Shyper.c
+++ b/src/H5Shyper.c
@@ -4725,11 +4725,21 @@ H5S__get_select_hyper_blocklist(H5S_t *space, hsize_t startblock, hsize_t numblo
                     startblock = 0;
                 } /* end else */
             }     /* end if */
-
+// FIXME: Crash CWE-125: Out-of-bounds Read
+            
+            /* Defensive checks for out-of-bounds before block processing */
+            if (buf == NULL || tmp_count == NULL || offset == NULL || end == NULL || diminfo == NULL || ndims <= 0 || fast_dim < 0 || fast_dim >= ndims) {
+                /* Fail safely */
+                break;
+            }
+            
             /* Iterate over the blocks in the fastest dimension */
             while (tmp_count[fast_dim] > 0 && numblocks > 0) {
                 /* Sanity check */
                 assert(startblock == 0);
+                
+                /* Bounds check for block counts */
+                if (tmp_count[fast_dim] <= 0) break;

                 /* Copy the starting location */
                 H5MM_memcpy(buf, offset, sizeof(hsize_t) * ndims);
