patch:
diff --git a/lib/Parser/JSParserImpl.cpp b/lib/Parser/JSParserImpl.cpp
index 0589efb3a..8255d69af 100644
--- a/lib/Parser/JSParserImpl.cpp
+++ b/lib/Parser/JSParserImpl.cpp
@@ -4234,7 +4234,7 @@ Optional<ESTree::Node *> JSParserImpl::parseConditionalExpression(
     if (!optConsequent)
       return None;
     consequent = *optConsequent;
-  }
+}

   if (!eat(
           TokenKind::colon,
@@ -4242,10 +4242,19 @@ Optional<ESTree::Node *> JSParserImpl::parseConditionalExpression(
           "in conditional expression after '... ? ...'",
           "location of '?'",
           questionRange.Start))
+// FIXME: Crash CWE-834: Excessive Iteration
+     return None;
+
+  static const int MAX_PARSE_DEPTH = 256;
+  static thread_local int parse_depth = 0;
+  if (++parse_depth > MAX_PARSE_DEPTH) {
+    --parse_depth;
+    // Exceeded max parsing recursion depth, prevent stack exhaustion
     return None;
-
+  }
   auto optAlternate = parseAssignmentExpression(
       param, AllowTypedArrowFunction::Yes, CoverTypedParameters::No);
+  --parse_depth;
   if (!optAlternate)
     return None;
   ESTree::Node *alternate = *optAlternate;
