patch:
diff --git a/lib/Regex/Executor.cpp b/lib/Regex/Executor.cpp
index fb73b8e56..b7b0fdadd 100644
--- a/lib/Regex/Executor.cpp
+++ b/lib/Regex/Executor.cpp
@@ -1316,7 +1316,18 @@ auto Context<Traits>::match(State<Traits> *s, bool onlyAtStart)
             // Save and restore the position because lookaheads do not consume
             // anything.
             s->ip_ += sizeof(LookaroundInsn);
+            
+            // Add recursion depth check to prevent stack overflow (CWE-834)
+            static const uint32_t MAX_RECURSION_DEPTH = 1000;
+            static thread_local uint32_t recursionDepth_ = 0;
+            
+            if (recursionDepth_ >= MAX_RECURSION_DEPTH) {
+              return ExecutionStatus::RETURNED;
+            }
+            recursionDepth_++;
             auto match = this->match(s, true /* onlyAtStart */);
+            recursionDepth_--;
+            
             // There were no errors and we matched something (so non-null
             // return)
             matched = match && match.getValue();
