-
-    return createGetSuperProperty(
-        memberExpression, topClass->parentClass, memberExpression->_property);
-  }
+if (topClass == nullptr || topClass->parentClass == nullptr) {
+  // Should not happen
+  return doVisitChildren(memberExpression);
+}

-  void visit(ESTree::Node *node) {
-    visitESTreeChildren(*this, node);
-  }
+return createGetSuperProperty(
+    memberExpression, topClass->parentClass, memberExpression->_property);
+}
+// FIXME: Crash CWE-834: Excessive Iteration
+
+void visit(ESTree::Node *node) {
+  static const int kMaxVisitDepth = 512;
+  static thread_local int visitDepth = 0;
+  if (++visitDepth > kMaxVisitDepth) {
+    // Prevent excessive iteration / stack overflow.
+    --visitDepth;
+    return;
+  }
+  visitESTreeChildren(*this, node);
+  --visitDepth;
+}

-  bool incRecursionDepth(ESTree::Node *) {
-    return true;
-  }
+bool incRecursionDepth(ESTree::Node *) {
+  return true;
+}

-  void decRecursionDepth() {}
+void decRecursionDepth() {}

