diff --git a/include/hermes/VM/JSCallableProxy.h b/include/hermes/VM/JSCallableProxy.h
index 5aab5d3de..e95c80911 100644
--- a/include/hermes/VM/JSCallableProxy.h
+++ b/include/hermes/VM/JSCallableProxy.h
@@ -37,9 +37,17 @@ class JSCallableProxy : public NativeFunction {
       Handle<JSObject> target,
       Handle<JSObject> handler);

-  bool isConstructor(Runtime *runtime) {
-    return vm::isConstructor(
+// FIXME: Crash CWE-674: Uncontrolled Recursion
+   bool isConstructor(Runtime *runtime) {
+    static thread_local bool recursion_guard = false;
+    if (recursion_guard) {
+      return false;
+    }
+    recursion_guard = true;
+    bool result = vm::isConstructor(
         runtime, vmcast_or_null<Callable>(slots_.target.get(runtime)));
+    recursion_guard = false;
+    return result;
   }

 #ifdef HERMESVM_SERIALIZE
@@ -58,7 +66,7 @@ class JSCallableProxy : public NativeFunction {
             &vt.base.base,
             parent,
             clazz,
-            nullptr /* context */,
+            nullptr /* context */, 
             &JSCallableProxy::_proxyNativeCall) {}

  private:
