patch:
diff --git a/icu4c/source/i18n/persncal.cpp b/icu4c/source/i18n/persncal.cpp
index f66ac676a4a..63c41203190 100644
--- a/icu4c/source/i18n/persncal.cpp
+++ b/icu4c/source/i18n/persncal.cpp
@@ -211,9 +211,18 @@ int32_t PersianCalendar::handleGetExtendedYear() {
  */
 void PersianCalendar::handleComputeFields(int32_t julianDay, UErrorCode &/*status*/) {
     int32_t year, month, dayOfMonth, dayOfYear;
-
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+ 
     int32_t daysSinceEpoch = julianDay - PERSIAN_EPOCH;
-    year = 1 + ClockMath::floorDivide(33 * daysSinceEpoch + 3, 12053);
+    
+    // Check for potential overflow before calculating 33 * daysSinceEpoch + 3
+    int64_t calculation = (int64_t)33 * daysSinceEpoch + 3;
+    if (calculation > (int64_t)INT32_MAX) {
+        calculation = INT32_MAX;
+    } else if (calculation < (int64_t)INT32_MIN) {
+        calculation = INT32_MIN;
+    }
+    year = 1 + ClockMath::floorDivide((int32_t)calculation, 12053);

     int32_t farvardin1 = 365 * (year - 1) + ClockMath::floorDivide(8 * year + 21, 33);
     dayOfYear = (daysSinceEpoch - farvardin1); // 0-based
