 void PersianCalendar::handleComputeFields(int32_t julianDay, UErrorCode &/*status*/) {
     int32_t year, month, dayOfMonth, dayOfYear;
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+    int64_t daysSinceEpoch = (int64_t)julianDay - (int64_t)PERSIAN_EPOCH;
+    int64_t temp = 33LL * daysSinceEpoch + 3LL;
+    if (temp > INT32_MAX || temp < INT32_MIN) {
+        // Handle overflow, clamp or error out as needed, here we clamp
+        temp = (temp > INT32_MAX) ? INT32_MAX : INT32_MIN;
+    }
+    year = 1 + ClockMath::floorDivide((int32_t)temp, 12053);

-    int32_t daysSinceEpoch = julianDay - PERSIAN_EPOCH;
-    year = 1 + ClockMath::floorDivide(33 * daysSinceEpoch + 3, 12053);
-
-    int32_t farvardin1 = 365 * (year - 1) + ClockMath::floorDivide(8 * year + 21, 33);
-    dayOfYear = (daysSinceEpoch - farvardin1); // 0-based
+    // Fix: Disambiguate floorDivide call by casting numerator and denominator to int32_t
+    int64_t farvardin1 = 365LL * (year - 1) + ClockMath::floorDivide((int32_t)(8LL * year + 21LL), (int32_t)33LL);
+    dayOfYear = (int32_t)(daysSinceEpoch - farvardin1); // 0-based
     if (dayOfYear < 216) { // Compute 0-based month
         month = dayOfYear / 31;
     } else {
