+// FIXME: Crash CWE-674: Uncontrolled Recursion
+             c = charAt(*offset, context);
             if (c == 0x5C /*'\\'*/ && ahead < length) {
+                // Limit recursion depth by checking bounds
+                static int recursion_depth = 0;
+                if (recursion_depth >= 50) {
+                    goto err;
+                }
+                recursion_depth++;
                 c = (UChar) u_unescapeAt(charAt, &ahead, length, context);
+                recursion_depth--;
             }
             if (U16_IS_TRAIL(c)) {
                 *offset = ahead;
