patch:
diff --git a/icu4c/source/common/ustring.cpp b/icu4c/source/common/ustring.cpp
index 7ab2e1bf891..ea99f413336 100644
--- a/icu4c/source/common/ustring.cpp
+++ b/icu4c/source/common/ustring.cpp
@@ -1292,9 +1292,40 @@ u_unescapeAt(UNESCAPE_CHAR_AT charAt,
          */
         if (*offset < length && U16_IS_LEAD(result)) {
             int32_t ahead = *offset + 1;
-            c = charAt(*offset, context);
+// FIXME: Crash CWE-674: Uncontrolled Recursion
+             c = charAt(*offset, context);
             if (c == 0x5C /*'\\'*/ && ahead < length) {
-                c = (UChar) u_unescapeAt(charAt, &ahead, length, context);
+                /* Use iterative approach instead of recursive call to prevent stack overflow */
+                UChar32 escaped_char = 0;
+                int32_t temp_offset = ahead;
+                /* Simple iterative parsing for single escape sequence */
+                if (temp_offset < length) {
+                    UChar next_char = charAt(temp_offset, context);
+                    if (next_char == 0x75 /*'u'*/ && temp_offset + 4 < length) {
+                        /* Parse \uXXXX format */
+                        int32_t hex_value = 0;
+                        int valid_hex = 1;
+                        for (int i = 0; i < 4; i++) {
+                            temp_offset++;
+                            if (temp_offset >= length) {
+                                valid_hex = 0;
+                                break;
+                            }
+                            UChar hex_char = charAt(temp_offset, context);
+                            int hex_digit = _digit16(hex_char);
+                            if (hex_digit < 0) {
+                                valid_hex = 0;
+                                break;
+                            }
+                            hex_value = (hex_value << 4) | hex_digit;
+                        }
+                        if (valid_hex && hex_value >= 0 && hex_value <= 0xFFFF) {
+                            escaped_char = (UChar32)hex_value;
+                            ahead = temp_offset + 1;
+                        }
+                    }
+                }
+                c = (UChar)escaped_char;
             }
             if (U16_IS_TRAIL(c)) {
                 *offset = ahead;
