patch:
diff --git a/icu4c/source/common/ustring.cpp b/icu4c/source/common/ustring.cpp
index 7ab2e1bf891..b8285343714 100644
--- a/icu4c/source/common/ustring.cpp
+++ b/icu4c/source/common/ustring.cpp
@@ -1278,7 +1278,7 @@ u_unescapeAt(UNESCAPE_CHAR_AT charAt,
             goto err;
         }
         if (braces) {
-            if (c != 0x7D /*}*/) {
+            if (c != 0x7D /*}*/){
                 goto err;
             }
             ++(*offset);
@@ -1290,15 +1290,25 @@ u_unescapeAt(UNESCAPE_CHAR_AT charAt,
          * there is a trail surrogate after it, either as an escape or
          * as a literal.  If so, join them up into a supplementary.
          */
-        if (*offset < length && U16_IS_LEAD(result)) {
-            int32_t ahead = *offset + 1;
-            c = charAt(*offset, context);
-            if (c == 0x5C /*'\\'*/ && ahead < length) {
-                c = (UChar) u_unescapeAt(charAt, &ahead, length, context);
-            }
-            if (U16_IS_TRAIL(c)) {
-                *offset = ahead;
-                result = U16_GET_SUPPLEMENTARY(result, c);
+        {
+            static const int ICU_UNESCAPE_RECURSION_LIMIT = 16;
+            static int recursion_depth = 0;
+            if (*offset < length && U16_IS_LEAD(result)) {
+                if (recursion_depth >= ICU_UNESCAPE_RECURSION_LIMIT) {
+                    goto err;
+                }
+                ++recursion_depth;
+                int32_t ahead = *offset + 1;
+// FIXME: Crash CWE-834: Excessive Iteration
+                 c = charAt(*offset, context);
+                if (c == 0x5C /*'\\'*/ && ahead < length) {
+                    c = (UChar) u_unescapeAt(charAt, &ahead, length, context);
+                }
+                --recursion_depth;
+                if (U16_IS_TRAIL(c)) {
+                    *offset = ahead;
+                    result = U16_GET_SUPPLEMENTARY(result, c);
+                }
             }
         }
         return result;
