patch:
diff --git a/icu4c/source/i18n/formatted_string_builder.cpp b/icu4c/source/i18n/formatted_string_builder.cpp
index 5aabc31cc43..43c2076136c 100644
--- a/icu4c/source/i18n/formatted_string_builder.cpp
+++ b/icu4c/source/i18n/formatted_string_builder.cpp
@@ -274,15 +274,22 @@ int32_t FormattedStringBuilder::prepareForInsertHelper(int32_t index, int32_t co
     int32_t oldCapacity = getCapacity();
     int32_t oldZero = fZero;
     char16_t *oldChars = getCharPtr();
-    Field *oldFields = getFieldPtr();
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+     Field *oldFields = getFieldPtr();
     if (fLength + count > oldCapacity) {
-        int32_t newCapacity = (fLength + count) * 2;
+        // Use int64_t for intermediate calculations to prevent overflow
+        int64_t newCapacityLong = (static_cast<int64_t>(fLength) + count) * 2;
+        // Check if the result fits in int32_t
+        if (newCapacityLong > INT32_MAX) {
+            status = U_MEMORY_ALLOCATION_ERROR;
+            return -1;
+        }
+        int32_t newCapacity = static_cast<int32_t>(newCapacityLong);
         int32_t newZero = newCapacity / 2 - (fLength + count) / 2;

         // C++ note: malloc appears in two places: here and in the assignment operator.
         auto newChars = static_cast<char16_t *> (uprv_malloc(sizeof(char16_t) * newCapacity));
-        auto newFields = static_cast<Field *>(uprv_malloc(sizeof(Field) * newCapacity));
-        if (newChars == nullptr || newFields == nullptr) {
+        auto newFields = static_cast<Field *>(uprv_malloc(sizeof(Field) * newCapacity));        if (newChars == nullptr || newFields == nullptr) {
             uprv_free(newChars);
             uprv_free(newFields);
             status = U_MEMORY_ALLOCATION_ERROR;
