patch:
diff --git a/icu4c/source/i18n/japancal.cpp b/icu4c/source/i18n/japancal.cpp
index 7e69b97a3af..65b4c6e6d3a 100644
--- a/icu4c/source/i18n/japancal.cpp
+++ b/icu4c/source/i18n/japancal.cpp
@@ -228,7 +228,26 @@ void JapaneseCalendar::handleComputeFields(int32_t julianDay, UErrorCode& status
     int32_t eraIdx = gJapaneseEraRules->getEraIndex(year, internalGetMonth(status) + 1, internalGet(UCAL_DAY_OF_MONTH), status);

     internalSet(UCAL_ERA, eraIdx);
-    internalSet(UCAL_YEAR, year - gJapaneseEraRules->getStartYear(eraIdx, status) + 1);
+    // Check for overflow before subtraction
+    int32_t startYear = gJapaneseEraRules->getStartYear(eraIdx, status);
+    if (U_SUCCESS(status)) {
+        // Check if subtraction would cause overflow
+        if (startYear > 0 && year < INT32_MIN + startYear) {
+            status = U_INVALID_FORMAT_ERROR;
+            return;
+        }
+        if (startYear < 0 && year > INT32_MAX + startYear) {
+            status = U_INVALID_FORMAT_ERROR;
+            return;
+        }
+        int32_t result = year - startYear + 1;
+        // Additional check for the final addition of 1
+        if (result == INT32_MAX) {
+            status = U_INVALID_FORMAT_ERROR;
+            return;
+        }
+        internalSet(UCAL_YEAR, result);
+    }
 }

 /*
