patch:
diff --git a/icu4c/source/common/messagepattern.cpp b/icu4c/source/common/messagepattern.cpp
index 9e318295f9a..cddd94ca6e8 100644
--- a/icu4c/source/common/messagepattern.cpp
+++ b/icu4c/source/common/messagepattern.cpp
@@ -641,12 +641,19 @@ MessagePattern::parseArg(int32_t index, int32_t argStartLength, int32_t nestingL
             }
         } else /* ',' */ {
             ++index;
+            // Recursion depth check to prevent stack overflow vulnerability
+            const int32_t MAX_NESTING = 64;
+            if(nestingLevel >= MAX_NESTING) {
+                setParseError(parseError, index);
+                errorCode = U_PARSE_ERROR_LIMIT;
+                return 0;
+            }
             if(argType==UMSGPAT_ARG_TYPE_SIMPLE) {
-                index=parseSimpleStyle(index, parseError, errorCode);
+                 index=parseSimpleStyle(index, parseError, errorCode);
             } else if(argType==UMSGPAT_ARG_TYPE_CHOICE) {
-                index=parseChoiceStyle(index, nestingLevel, parseError, errorCode);
+                index=parseChoiceStyle(index, nestingLevel + 1, parseError, errorCode);
             } else {
-                index=parsePluralOrSelectStyle(argType, index, nestingLevel, parseError, errorCode);
+                index=parsePluralOrSelectStyle(argType, index, nestingLevel + 1, parseError, errorCode);
             }
         }
     }
