+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
 int32_t Calendar::handleGetMonthLength(int32_t extendedYear, int32_t month, UErrorCode& status) const
 {
-    return handleComputeMonthStart(extendedYear, month+1, true, status) -
-        handleComputeMonthStart(extendedYear, month, true, status);
+    int64_t nextMonthStart = handleComputeMonthStart(extendedYear, (int64_t)month+1, true, status);
+    int64_t thisMonthStart = handleComputeMonthStart(extendedYear, (int64_t)month, true, status);
+    int64_t diff = nextMonthStart - thisMonthStart;
+    if (diff > INT32_MAX || diff < INT32_MIN) {
+        status = U_INTERNAL_PROGRAM_ERROR;
+        return 0;
+    }
+    return static_cast<int32_t>(diff);
 }

