+                        } /* correction needed */
+                    } else {
+                        status = U_ILLEGAL_ARGUMENT_ERROR;
+                        return 0;
+                    }
                 } /* leastmaximum */
             } /* resolvefields(year) != year_woy */
         } /* bestfield != week_of_year */
