patch:
diff --git a/icu4c/source/i18n/chnsecal.cpp b/icu4c/source/i18n/chnsecal.cpp
index fb8e5191c52..1a98f1ca943 100644
--- a/icu4c/source/i18n/chnsecal.cpp
+++ b/icu4c/source/i18n/chnsecal.cpp
@@ -1043,9 +1043,23 @@ void ChineseCalendar::offsetMonth(int32_t newMoon, int32_t dayOfMonth, int32_t d
     if (U_FAILURE(status)) {
         return;
     }
-
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+ 
     // Find the target dayOfMonth
-    int32_t jd = newMoon + kEpochStartAsJulianDay - 1 + dayOfMonth;
+    int32_t temp_sum1 = 0;
+    int32_t temp_sum2 = 0;
+    if (((kEpochStartAsJulianDay > 0) && (newMoon > INT32_MAX - kEpochStartAsJulianDay)) ||
+        ((kEpochStartAsJulianDay < 0) && (newMoon < INT32_MIN - kEpochStartAsJulianDay))) {
+        status = U_ILLEGAL_ARGUMENT_ERROR;
+        return;
+    }
+    temp_sum1 = newMoon + kEpochStartAsJulianDay;
+    if ((temp_sum1 > 0 && dayOfMonth > INT32_MAX - temp_sum1 + 1) ||
+        (temp_sum1 < 0 && dayOfMonth < INT32_MIN - temp_sum1 + 1)) {
+        status = U_ILLEGAL_ARGUMENT_ERROR;
+        return;
+    }
+    int32_t jd = temp_sum1 - 1 + dayOfMonth;

     // Pin the dayOfMonth.  In this calendar all months are 29 or 30 days
     // so pinning just means handling dayOfMonth 30.
