patch:
diff --git a/icu4c/source/i18n/calendar.cpp b/icu4c/source/i18n/calendar.cpp
index e713fb0195e..12d71fde483 100644
--- a/icu4c/source/i18n/calendar.cpp
+++ b/icu4c/source/i18n/calendar.cpp
@@ -3609,7 +3609,13 @@ int32_t Calendar::handleComputeJulianDay(UCalendarDateFields bestField, UErrorCo
 #endif
                     if(julianDay+testDate > nextJulianDay) { // is it past Dec 31?  (nextJulianDay is day BEFORE year+1's  Jan 1)
                         // Fire up the calculating engines.. retry YWOY = (year-1)
-                        julianDay = handleComputeMonthStart(year-1, 0, false, status); // jd before Jan 1 of previous year
+                        // Check for integer underflow when subtracting 1 from year
+                        if (year == INT32_MIN) {
+                            status = U_ILLEGAL_ARGUMENT_ERROR;
+                            return 0;
+                        }
+                        int32_t prevYear = year - 1;
+                        julianDay = handleComputeMonthStart(prevYear, 0, false, status); // jd before Jan 1 of previous year
                         if (U_FAILURE(status)) {
                             return 0;
                         }
@@ -3622,7 +3628,7 @@ int32_t Calendar::handleComputeJulianDay(UCalendarDateFields bestField, UErrorCo

 #if defined (U_DEBUG_CAL)
                         fprintf(stderr, "%s:%d - date now %d, jd%d, ywoy%d\n",
-                            __FILE__, __LINE__, date, julianDay, year-1);
+                            __FILE__, __LINE__, date, julianDay, prevYear);
 #endif
