patch:
diff --git a/icu4c/source/i18n/calendar.cpp b/icu4c/source/i18n/calendar.cpp
index e713fb0195e..4962d58626b 100644
--- a/icu4c/source/i18n/calendar.cpp
+++ b/icu4c/source/i18n/calendar.cpp
@@ -1338,6 +1338,13 @@ Calendar::complete(UErrorCode& status)
     if (U_FAILURE(status)) {
        return;
     }
+    // Guard against possible integer underflow in future calendar arithmetic
+    for (int i = 0; i < UCAL_FIELD_COUNT; ++i) {
+        if (fFields[i] == INT32_MIN) {
+            status = U_ILLEGAL_ARGUMENT_ERROR;
+            return;
+        }
+    }
     if (!fIsTimeSet) {
         updateTime(status);
         /* Test for buffer overflows */
