diff --git a/icu4c/source/i18n/calendar.cpp b/icu4c/source/i18n/calendar.cpp
index e713fb0195e..a8bf1ccb953 100644
--- a/icu4c/source/i18n/calendar.cpp
+++ b/icu4c/source/i18n/calendar.cpp
@@ -1338,6 +1338,13 @@ Calendar::complete(UErrorCode& status)
     if (U_FAILURE(status)) {
        return;
     }
+    // Check for INT_MIN in fFields to avoid integer overflows/wraparounds
+    for (int i = 0; i < UCAL_FIELD_COUNT; ++i) {
+        if (this->fFields[i] == std::numeric_limits<int>::min()) {
+            status = U_ILLEGAL_ARGUMENT_ERROR;
+            return;
+        }
+    }
     if (!fIsTimeSet) {
         updateTime(status);
         /* Test for buffer overflows */
@@ -1356,6 +1363,7 @@ Calendar::complete(UErrorCode& status)
     }
 }

+
 //-------------------------------------------------------------------------
 // Protected utility methods for use by subclasses.  These are very handy
 // for implementing add, roll, and computeFields.
