@@ -324,9 +324,16 @@ NFRule::parseRuleDescriptor(UnicodeString& description, UErrorCode& status)
                 ++p;
                 ll_10 = 10;
                 while (p < descriptorLength) {
-                    c = descriptor.charAt(p);
+// FIXME: Crash CWE-190: Integer Overflow or Wraparound
+                     c = descriptor.charAt(p);
                     if (c >= gZero && c <= gNine) {
-                        val = val * ll_10 + static_cast<int32_t>(c - gZero);
+                        // Check for overflow before multiplication
+                        int64_t digit = static_cast<int32_t>(c - gZero);
+                        if (val > (INT64_MAX - digit) / ll_10) {
+                            status = U_PARSE_ERROR;
+                            return;
+                        }
+                        val = val * ll_10 + digit;
                     }
                     else if (c == gGreaterThan) {
                         break;
