patch:
diff --git a/src/libjasper/heic/heic_dec.c b/src/libjasper/heic/heic_dec.c
index cde3843..45e3e50 100644
--- a/src/libjasper/heic/heic_dec.c
+++ b/src/libjasper/heic/heic_dec.c
@@ -209,7 +209,7 @@ jas_image_t *jas_heic_decode(jas_stream_t *in, const char *optstr)
 		jas_logerrorf(
 		  "maximum number of samples would be exceeded (%zu > %zu)\n",
 		  num_samples, opts.max_samples);
-		goto error;
+	goto error;
 	}

 	/* Should I use:
@@ -217,8 +217,14 @@ jas_image_t *jas_heic_decode(jas_stream_t *in, const char *optstr)
 	*/

 	/* Decode the image and convert the colorspace to RGB,
-	  saved as 24bit interleaved. */
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+ 	  saved as 24bit interleaved. */
 	struct heif_error err;
+	// Add validation for handle parameter before calling heif_decode_image
+	if (!handle) {
+		jas_logerrorf("handle is NULL, cannot decode image\n");
+		goto error;
+	}
 	err = heif_decode_image(handle, &img, heif_colorspace_RGB,
 	  heif_chroma_interleaved_RGB, 0);
 	if (err.code != 0) {
@@ -230,7 +236,6 @@ jas_image_t *jas_heic_decode(jas_stream_t *in, const char *optstr)
 	enum heif_chroma chroma = heif_image_get_chroma_format(img);
 	assert(chroma == heif_chroma_interleaved_RGB);
 	JAS_LOGDEBUGF(1, "colorspace %d; chroma %d\n", colorspace, chroma);
-
 	int stride;
 	const uint8_t* data = heif_image_get_plane_readonly(img,
 	  heif_channel_interleaved, &stride);
