patch:
diff --git a/src/libjasper/heic/heic_dec.c b/src/libjasper/heic/heic_dec.c
index cde3843..a55dbbe 100644
--- a/src/libjasper/heic/heic_dec.c
+++ b/src/libjasper/heic/heic_dec.c
@@ -217,14 +217,25 @@ jas_image_t *jas_heic_decode(jas_stream_t *in, const char *optstr)
 	*/

 	/* Decode the image and convert the colorspace to RGB,
-	  saved as 24bit interleaved. */
+// FIXME: Crash CWE-476: NULL Pointer Dereference
+ 	  saved as 24bit interleaved. */
 	struct heif_error err;
+	// Validate handle pointer before use
+	if (handle == NULL) {
+		jas_logerrorf("heif_decode_image: handle is NULL\n");
+		goto error;
+	}
 	err = heif_decode_image(handle, &img, heif_colorspace_RGB,
 	  heif_chroma_interleaved_RGB, 0);
 	if (err.code != 0) {
 		jas_logerrorf("heif_decode_image failed\n");
 		goto error;
 	}
+	// Validate img pointer after heif_decode_image call
+	if (img == NULL) {
+		jas_logerrorf("heif_decode_image returned NULL image\n");
+		goto error;
+	}

 	enum heif_colorspace colorspace = heif_image_get_colorspace(img);
 	enum heif_chroma chroma = heif_image_get_chroma_format(img);
