patch:
diff --git a/clang/lib/Sema/SemaDeclCXX.cpp b/clang/lib/Sema/SemaDeclCXX.cpp
index 2142582adf6e..e124966ad8bd 100644
--- a/clang/lib/Sema/SemaDeclCXX.cpp
+++ b/clang/lib/Sema/SemaDeclCXX.cpp
@@ -9005,10 +9005,17 @@ bool Sema::CheckExplicitlyDefaultedComparison(Scope *S, FunctionDecl *FD,
                             int(1)))
       return true;

-    if (llvm::none_of(RD->friends(), [&](const FriendDecl *F) {
-          return FD->getCanonicalDecl() ==
-                 F->getFriendDecl()->getCanonicalDecl();
-        })) {
+// Use a traditional loop to safely check friend declarations and avoid pointer expiration
+     bool foundFriend = false;
+     for (const FriendDecl *F : RD->friends()) {
+       if (F && F->getFriendDecl() && 
+           FD->getCanonicalDecl() == F->getFriendDecl()->getCanonicalDecl()) {
+         foundFriend = true;
+         break;
+       }
+     }
+     
+     if (!foundFriend) {
       Diag(FD->getLocation(), diag::err_defaulted_comparison_not_friend)
           << int(DCK) << int(0) << RD;
       Diag(RD->getCanonicalDecl()->getLocation(), diag::note_declared_at);
