patch:
diff --git a/llvm/lib/Transforms/Vectorize/SLPVectorizer.cpp b/llvm/lib/Transforms/Vectorize/SLPVectorizer.cpp
index 4dc398f716b3..947c31e5926c 100644
--- a/llvm/lib/Transforms/Vectorize/SLPVectorizer.cpp
+++ b/llvm/lib/Transforms/Vectorize/SLPVectorizer.cpp
@@ -15111,7 +15111,14 @@ Instruction &BoUpSLP::getLastInstructionInBundle(const TreeEntry *E) {
       Res = (*It)->getBundle().back()->getInst();
       return *Res;
     }
-    assert(E->getOpcode() == Instruction::PHI && "Expected PHI");
+    if (E->getOpcode() != Instruction::PHI) {
+      for (Value *V : E->Scalars) {
+        if (Instruction *I = dyn_cast<Instruction>(V)) {
+          Res = I;
+        }
+      }
+      return *Res;
+    }
   }

   // LastInst can still be null at this point if there's either not an entry
