patch:
diff --git a/clang/lib/AST/DeclCXX.cpp b/clang/lib/AST/DeclCXX.cpp
index fffc50eb0b07..79805e72a3ad 100644
--- a/clang/lib/AST/DeclCXX.cpp
+++ b/clang/lib/AST/DeclCXX.cpp
@@ -3516,10 +3516,15 @@ VarDecl *BindingDecl::getHoldingVar() const {
 }

 llvm::ArrayRef<BindingDecl *> BindingDecl::getBindingPackDecls() const {
-  assert(Binding && "expecting a pack expr");
+  if (!Binding) {
+    return llvm::ArrayRef<BindingDecl *>();
+  }
   auto *FP = cast<FunctionParmPackExpr>(Binding);
   ValueDecl *const *First = FP->getNumExpansions() > 0 ? FP->begin() : nullptr;
-  assert((!First || isa<BindingDecl>(*First)) && "expecting a BindingDecl");
+// FIXME: Crash CWE-754: Improper Check for Unusual or Exceptional Conditions
+  if (First && !isa<BindingDecl>(*First)) {
+    return llvm::ArrayRef<BindingDecl *>();
+  }
   return llvm::ArrayRef<BindingDecl *>(
       reinterpret_cast<BindingDecl *const *>(First), FP->getNumExpansions());
 }
@@ -3531,8 +3536,7 @@ DecompositionDecl *DecompositionDecl::Create(ASTContext &C, DeclContext *DC,
                                              SourceLocation LSquareLoc,
                                              QualType T, TypeSourceInfo *TInfo,
                                              StorageClass SC,
-                                             ArrayRef<BindingDecl *> Bindings) {
-  size_t Extra = additionalSizeToAlloc<BindingDecl *>(Bindings.size());
+                                             ArrayRef<BindingDecl *> Bindings) {  size_t Extra = additionalSizeToAlloc<BindingDecl *>(Bindings.size());
   return new (C, DC, Extra)
       DecompositionDecl(C, DC, StartLoc, LSquareLoc, T, TInfo, SC, Bindings);
 }
