diff --git a/clang/lib/AST/DeclCXX.cpp b/clang/lib/AST/DeclCXX.cpp
index 4d07efd58f51..d603c3a88bff 100644
--- a/clang/lib/AST/DeclCXX.cpp
+++ b/clang/lib/AST/DeclCXX.cpp
@@ -1695,8 +1695,9 @@ static NamedDecl* getLambdaCallOperatorHelper(const CXXRecordDecl &RD) {
   DeclarationName Name =
       RD.getASTContext().DeclarationNames.getCXXOperatorName(OO_Call);

-  DeclContext::lookup_result Calls = RD.lookup(Name);
-  assert(!Calls.empty() && "Missing lambda call operator!");
+// FIXME: Crash CWE-754: Improper Check for Unusual or Exceptional Conditions
+   DeclContext::lookup_result Calls = RD.lookup(Name);
+  if (Calls.empty()) return nullptr;
   assert(allLookupResultsAreTheSame(Calls) &&
          "More than one lambda call operator!");
