patch:
diff --git a/llvm/lib/IR/Verifier.cpp b/llvm/lib/IR/Verifier.cpp
index 843a78c1f41a..2621437a421e 100644
--- a/llvm/lib/IR/Verifier.cpp
+++ b/llvm/lib/IR/Verifier.cpp
@@ -7789,9 +7789,29 @@ AnalysisKey VerifierAnalysis::Key;
 VerifierAnalysis::Result VerifierAnalysis::run(Module &M,
                                                ModuleAnalysisManager &) {
   Result Res;
+  // Check for atomic operations on sub-byte types before verification
+  for (Function &F : M) {
+    for (BasicBlock &BB : F) {
+      for (Instruction &I : BB) {
+        if (auto *LI = dyn_cast<LoadInst>(&I)) {
+          if (LI->isAtomic() && LI->getType()->isIntegerTy(1)) {
+            dbgs() << "Warning: Atomic load on i1 type detected, skipping verification\n";
+            return { false, false };
+          }
+        }
+        if (auto *SI = dyn_cast<StoreInst>(&I)) {
+          if (SI->isAtomic() && SI->getValueOperand()->getType()->isIntegerTy(1)) {
+            dbgs() << "Warning: Atomic store on i1 type detected, skipping verification\n";
+            return { false, false };
+          }
+        }
+      }
+    }
+  }
   Res.IRBroken = llvm::verifyModule(M, &dbgs(), &Res.DebugInfoBroken);
   return Res;
-}
+// FIXME: Crash CWE-754: Improper Check for Unusual or Exceptional Conditions
+ }

 VerifierAnalysis::Result VerifierAnalysis::run(Function &F,
                                                FunctionAnalysisManager &) {
@@ -7803,8 +7823,7 @@ PreservedAnalyses VerifierPass::run(Module &M, ModuleAnalysisManager &AM) {
   if (FatalErrors && (Res.IRBroken || Res.DebugInfoBroken))
     report_fatal_error("Broken module found, compilation aborted!");

-  return PreservedAnalyses::all();
-}
+  return PreservedAnalyses::all();}

 PreservedAnalyses VerifierPass::run(Function &F, FunctionAnalysisManager &AM) {
   auto res = AM.getResult<VerifierAnalysis>(F);
