patch:
diff --git a/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp b/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
index 6027572f1e84..c5f2829b43e3 100644
--- a/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
+++ b/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
@@ -2125,9 +2125,15 @@ void VPScalarIVStepsRecipe::execute(VPTransformState &State) {
     if (getUnrollPart(*this) != 1) {
       StartIdx0 =
           Builder.CreateMul(StartIdx0, ConstantInt::get(StartIdx0->getType(),
-                                                        getUnrollPart(*this)));
+                                                         getUnrollPart(*this)));
+    }
+    // Check if truncation is valid before attempting it
+    if (CastInst::castIsValid(Instruction::Trunc, StartIdx0, IntStepTy)) {
+      StartIdx0 = Builder.CreateTrunc(StartIdx0, IntStepTy);
+    } else {
+      // Use zero-extension followed by truncation as fallback
+      StartIdx0 = Builder.CreateZExtOrTrunc(StartIdx0, IntStepTy);
     }
-    StartIdx0 = Builder.CreateTrunc(StartIdx0, IntStepTy);
   }

   if (!FirstLaneOnly && State.VF.isScalable()) {
