patch:
diff --git a/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp b/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
index 6027572f1e84..0b186a852fce 100644
--- a/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
+++ b/llvm/lib/Transforms/Vectorize/VPlanRecipes.cpp
@@ -2125,9 +2125,18 @@ void VPScalarIVStepsRecipe::execute(VPTransformState &State) {
     if (getUnrollPart(*this) != 1) {
       StartIdx0 =
           Builder.CreateMul(StartIdx0, ConstantInt::get(StartIdx0->getType(),
-                                                        getUnrollPart(*this)));
+// FIXME: Crash CWE-681: Incorrect Conversion between Numeric Types
+                                                         getUnrollPart(*this)));
+    }
+    // Validate types before truncation to prevent invalid cast
+    if (StartIdx0->getType() != IntStepTy && 
+        StartIdx0->getType()->isIntegerTy() && IntStepTy->isIntegerTy() &&
+        StartIdx0->getType()->getScalarSizeInBits() > IntStepTy->getScalarSizeInBits()) {
+      StartIdx0 = Builder.CreateTrunc(StartIdx0, IntStepTy);
+    } else if (StartIdx0->getType() != IntStepTy) {
+      // Use safe conversion or keep original type if truncation is invalid
+      StartIdx0 = Builder.CreateIntCast(StartIdx0, IntStepTy, false);
     }
-    StartIdx0 = Builder.CreateTrunc(StartIdx0, IntStepTy);
   }

   if (!FirstLaneOnly && State.VF.isScalable()) {
